package desafio_1;

import java.util.ArrayList;

// Números que tem reversos,são aqueles em que todos seus algarismos são diferentes entre si,não começa com zero,e,
// seu reverso também não começa com zero
public class Desafio_1 {
		// variável inteira
		private static int x=0;
		public static void main(String[] args) {
			// TODO Auto-generated method stub	
			final int  CONST=1000000;
			ArrayList<String> lista1 = new ArrayList<>(); // lista dos números que tem reversos
			ArrayList<String> lista2 = new ArrayList<>(); // lista dos reversos dos números que tem reverso
			boolean existeReverso=false,jaExiste = false; // variáveis booleanas				
			// variáveis String
			String str1;
			String str2;			
			System.out.println(" Conjunto de pares(número + seu reverso) onde a soma entre eles dá ímpar: "+"\n");			
			for(int i=10;i<CONST;i++) {
				existeReverso=true; // inicializa a variável  "existeReverso"
				str2=""; // inicializa a variável str2
				str1=String.valueOf(i);// transforma o numeral - i - em uma representação string e atribui a variável str1 do tipo String
				// verifica se todos dígitos do numeral - i - são diferentes entre si
				for(int j=0;j<str1.length()-1;j++) {				
					for(int m=j+1;m<str1.length();m++) {				
						if(str1.charAt(j) == str1.charAt(m)) {			
							existeReverso=false;// se existem dígitos iguais então não há reverso						
						}
					}
				}				
				if(existeReverso){
					// obtém o reverso 
					for(int k=0;k<str1.length();k++) {
						str2=str2+(String.valueOf(str1.charAt(str1.length()-1-k)));// str2 é o reverso do str1			
					}	
					// somente para reverso que não inicia com zero
					if(str2.codePointAt(0) != 48) {
						lista1.add(str1);
						if(!lista2.isEmpty()) {
							for(String item: lista2) {
							    if(str1.equals(item)) { // verifica se str1 já existe na lista de reversos
								jaExiste=true;								
							    }
							}
							if(!jaExiste) { // se o numero já existe na lista dos reversos,não é impresso
								impressao(str1,str2,lista2);
							}
							
						}else {	// somente para lista vazia de reversos									
							 impressao(str1,str2,lista2);	
						      }
					}
				}
				jaExiste=false;			
			}						
			System.out.println("\n\n");
			System.out.println("Quantidade = "+x);
			
		}
		private  static void impressao(String str1,String str2,ArrayList lista2) {		
			lista2.add(str2);							
			int number = Integer.parseInt(str1)+Integer.parseInt(str2);// soma o número com seu reverso				
			if(number%2 != 0) { // verifica se a soma é ímpar
				System.out.printf("%s %s| ",str1,str2);// imprimi os números cuja a soma entre eles é ímpar	
				x+=1;
			}
		}
}
